//Define ui element
let clientNumber = document.getElementById("client");
let ClientIndex = 0;
setInterval(()=>{
  if(ClientIndex === 80){
    clearInterval();
  }else{
    ClientIndex += 1;
    clientNumber.innerHTML = ClientIndex+"+";
  }
},36);

let x = document.getElementById('pro');
let y = 0;
 setInterval(()=>{
    if( y === 200){
        clearInterval();
    }else{
        y += 1;
        x.innerHTML = y+"+";
    }
 },15);

 let R = document.getElementById("review");
 let C = 0;
 setInterval(()=>{
    if(C === 50){
        clearInterval();
    }else{
        C += 1;
        R.innerHTML = C + "+";
    }
 },65);

